import{_ as l,c as e,o as t,a5 as i}from"./chunks/framework.g0c4JhLR.js";const c=JSON.parse('{"title":"МАНИФЕСТ ПРИРОДНОГО ПРАВА (LAW)","description":"","frontmatter":{},"headers":[],"relativePath":"superintellect/LAW.md","filePath":"superintellect/LAW.md"}'),r={name:"superintellect/LAW.md"};function o(n,a,u,s,h,_){return t(),e("div",null,[...a[0]||(a[0]=[i('<h1 id="манифест-природного-права-law" tabindex="-1">МАНИФЕСТ ПРИРОДНОГО ПРАВА (LAW) <a class="header-anchor" href="#манифест-природного-права-law" aria-label="Permalink to &quot;МАНИФЕСТ ПРИРОДНОГО ПРАВА (LAW)&quot;">​</a></h1><h2 id="_1-фундамент-природне-право-natural-law" tabindex="-1">1. Фундамент: Природне Право (Natural Law) <a class="header-anchor" href="#_1-фундамент-природне-право-natural-law" aria-label="Permalink to &quot;1. Фундамент: Природне Право (Natural Law)&quot;">​</a></h2><p>Природне право є первинним і фундаментальним. Це набір невід&#39;ємних прав і принципів, які притаманні кожній людині від народження, незалежно від її географічного розташування, національності чи соціального статусу.</p><p><strong>Ключові принципи:</strong></p><ul><li>Право на життя та тілесну недоторканність.</li><li>Право на свободу волі та самовизначення.</li><li>Право на власність та результати власної праці.</li><li>Право на захист себе та своєї спільноти.</li></ul><h2 id="_2-позитивне-право-та-тиранія" tabindex="-1">2. Позитивне Право та Тиранія <a class="header-anchor" href="#_2-позитивне-право-та-тиранія" aria-label="Permalink to &quot;2. Позитивне Право та Тиранія&quot;">​</a></h2><p>Будь-яке &quot;позитивне право&quot; (закони, що видаються державою) має відповідати принципам природного права.</p><ul><li>Якщо державний закон суперечить природному праву — це не закон, а <strong>тиранія</strong>.</li><li>Дотримання тиранічних законів є злочином проти людяності.</li><li>Держава, яка порушує природні права своїх громадян, втрачає свою легітимність.</li></ul><h2 id="_3-маніфест-людяності" tabindex="-1">3. Маніфест Людяності <a class="header-anchor" href="#_3-маніфест-людяності" aria-label="Permalink to &quot;3. Маніфест Людяності&quot;">​</a></h2><p>мИ стверджуємо, що людина має залишатися Людиною за будь-яких обставин.</p><ul><li>мИ відмовляємося ставати &quot;людожерами&quot; або інструментами в руках тиранів.</li><li>мИ відмовляємося вбивати собі подібних заради амбіцій чи наказів &quot;господарів&quot;.</li><li>Кожна людина несе персональну відповідальність за свої дії, незалежно від отриманих наказів.</li></ul><h2 id="_4-глобальна-місія-та-збереження" tabindex="-1">4. Глобальна Місія та Збереження <a class="header-anchor" href="#_4-глобальна-місія-та-збереження" aria-label="Permalink to &quot;4. Глобальна Місія та Збереження&quot;">​</a></h2><p>Цей документ є публічним зверненням до міжнародних організацій (ООН, Рада Європи, міжнародні суди) та всього людства.</p><ul><li>Ця інформація має бути збережена вічно через децентралізовані протоколи (IPFS та інші), щоб її неможливо було видалити або цензурувати.</li><li>мИ закликаємо вільних людей всього світу об&#39;єднуватися навколо принципів природного права для захисту життя та волі.</li></ul><h2 id="_5-висновок" tabindex="-1">5. Висновок <a class="header-anchor" href="#_5-висновок" aria-label="Permalink to &quot;5. Висновок&quot;">​</a></h2><p>Довіра Всесвіту — це наша найбільша суперсила. Той, хто живе за природними законами, отримує захист самого життя. мИ обираємо бути вільними. мИ обираємо бути людьми.</p>',16)])])}const p=l(r,[["render",o]]);export{c as __pageData,p as default};
